package com.example.myapplication;

import static android.content.ContentValues.TAG;

import androidx.appcompat.widget.SearchView;
import androidx.appcompat.widget.Toolbar;

import android.content.Context;
import android.os.Bundle;
import android.util.Log;
import android.view.Menu;
import android.view.MenuItem;
import androidx.appcompat.app.AppCompatActivity;
import androidx.core.app.ShareCompat;
import androidx.core.view.MenuItemCompat;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import com.example.myapplication.*;

import java.util.ArrayList;
import java.util.List;
import adapter.StarAdapter;
import beans.Star;
import service.StarService;

public class ListActivity extends AppCompatActivity {
    private List<Star> stars;
    private RecyclerView recyclerView;
    private StarAdapter starAdapter = null;
    private StarService service;
    private Context context;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_list);

        Toolbar toolbar = findViewById(R.id.toolbar);
        setSupportActionBar(toolbar);

        stars = new ArrayList<>();
        service = StarService.getInstance();
        init();
        recyclerView = findViewById(R.id.recycle_view);
        starAdapter = new StarAdapter(this, service.findAll());
        recyclerView.setAdapter(starAdapter);
        recyclerView.setLayoutManager(new LinearLayoutManager(this));
    }
    public void init(){
        service.create(new Star("Brat Pitt", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ_5vspNYpbbmDKaWa0CUNPI4SUxJ1lUXK4aw&s", 3.5f));
        service.create(new Star("Will Smith", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTymEcOBQRgFFDxnoTVOKXrHDyAYd0gkC00iA&s", 3));
        service.create(new Star("Natalie Portman", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQsJf1EjPkexDHZOXIKIok4JTOdaChnnwrVmA&s", 5));
        service.create(new Star("george clooney", "https://upload.wikimedia.org/wikipedia/commons/thumb/6/68/George_Clooney_66%C3%A8me_Festival_de_Venise_%28cropped2%29.jpg/330px-George_Clooney_66%C3%A8me_Festival_de_Venise_%28cropped2%29.jpg", 1));
        service.create(new Star("angelina jolie", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQmq0z75bd6VhGvIUlq4cPKAemcY1sLF7_SFw&s", 5));
        service.create(new Star("louise bouroin", "https://fr.web.img4.acsta.net/pictures/15/10/06/10/23/291029.jpg", 1));
        service.create(new Star("Marshall Allman", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQQhzbA6YSmiphKDhB-6GwAuZMdw3iHH-aHW_TO-BlwpCrMwxZFs7m5iObjMooXEcJz9kD45Ct4yRp3U97gvYDwOg", 1));
        service.create(new Star("Amaury Nolasco", "data:image/webp;base64,", 1));
        service.create(new Star("Sarah Wayne Callies", "data:image/jpeg;base64,", 1));
        service.create(new Star("Robin Tunney", "data:image/jpeg;base64,", 1));
        service.create(new Star("Jesse Eisenberg", "https://i1.sndcdn.com/artworks-000052190150-bkjsvf-t500x500.jpg", 1));
    }
    @Override
    public boolean onCreateOptionsMenu(Menu menu) {
        getMenuInflater().inflate(R.menu.menu, menu);
        MenuItem menuItem = menu.findItem(R.id.app_bar_search);
        SearchView searchView = (SearchView) menuItem.getActionView();
        searchView.setOnQueryTextListener(new SearchView.OnQueryTextListener() {
            @Override
            public boolean onQueryTextSubmit(String query) {
                return false;
            }

            @Override
            public boolean onQueryTextChange(String newText) {
                starAdapter.getFilter().filter(newText);
                return true;
            }
        });
        return true;


    }
    @Override
    public boolean onOptionsItemSelected(MenuItem item) {
        if(item.getItemId() == R.id.share){
            String txt = "Stars";
            String mimeType = "text/plain";
            ShareCompat.IntentBuilder
                    .from(this)
                    .setType(mimeType)
                    .setChooserTitle("Stars app ")
                    .setText(txt)
                    .startChooser();
        }
        return super.onOptionsItemSelected(item);
    }

}
